import React, { useState } from 'react';
import postData from '../helpers/postData';

const FormComment = ({ idRestaurant }) => {
  const [ state, setState ] = useState({
    name: "",
    comment: ""
  });

  const handleInputChange = e => {
    setState({
      ...state,
      [e.target.name]: e.target.value
    });
  }

  const handleSubmit = e => {
    e.preventDefault();
    if(state.name !== "" && state.comment !== "") {
      const URL = '';
      const data = { user: state.name, comment: state.comment, idRestaurant: idRestaurant }
      postData(URL, data)
        .then( res => {

        })
        .catch( e => {
          console.log('Error:', e);
        });
    } else {
      console.log('vacio')
    }
  }

  return(
    <section>
        <h3 className="section-title">Escribir nuevo comentario</h3>
        <form className="form" onSubmit={ handleSubmit }>
          <div className="form-group">
            <label>Nombre</label>
            <input type="text" name="name" defaultValue={ state.name } onChange={ handleInputChange }></input>
          </div>
          <div className="form-group">
            <label>Comentario</label>
            <input type="text" name="name" defaultValue={ state.name } onChange={ handleInputChange }></input>
          </div>
          <button type="button">Enviar</button>
        </form>
    </section>
  )
}

export default FormComment;
