const postData = async (url, data) => {
  const res = await fetch( url , {
    method: 'POST',
    mode: 'no-cors',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: { data }
  })
  return res.json();
}

export default postData;
