import React from 'react';
import CardRestaurant from '../components/CardRestaurant';
import useFetchData from '../hooks/useFetchData';


const Home = () => {
  const URL = 'http://localhost:4000/api/restaurants/all';
  const { data, loading } = useFetchData(URL);
  return(
    <>
      <section>
        <h2 className="section-title">Lex Ortiz Ortiz Caniz√°lez</h2>
        <p>Description</p>
      </section>

      <section>
        <h2 className="section-title">Restaurantes</h2>
        { (loading) ?  <p>Cargando...</p> :
          ((data === {}) ? <p>No hay restaurantes para mostrar.</p> :

          <div className="resturants-list">
            { data.map((restaurant) => (
              <CardRestaurant key={restaurant._id} />
            ))}
          </div>

          )
        }
      </section>
    </>
  )
}

export default Home;
