      <section>
        <h2 className="section-title">Restaurantes</h2>
        { (loading) ?
          <p>Cargando...</p> :
          (
          <div className="resturants-list">
            data.map((restaurant, index))
          </div>

          // (data === {}) ? <p>No hay restaurantes para mostrar.</p> : null
          )
        }
      </section>
    </>
  )
}

export default Home;
