import React from 'react';
import useFetchData from '../hooks/useFetchData';
import RestaurantInfo from '../components/Restaurant';
import Gallery from '../components/Gallery';
import Comment from '../components/Restaurant';


const Restaurant = (props) => {
  const URL = "http://localhost:4000/api/restaurants/";
  const { data, loading } = useFetchData(URL + props.match.params.id);

  return(
    <>
      <section>
        {
          (loading) ?  <p>Cargando...</p> :
          ((data.length === 0) ? <p>No hay información para mostrar.</p> :
            <RestaurantInfo logo={ data.logo } name={ data.name } description={ data.description } />
          )
        }
      </section>

      <section>
        { (loading) ?  <p>Cargando...</p> :
          ((data.length === 0) ? <p>No hay información para mostrar.</p> : <Restaurant logo={ data.logo } logo={ data.logo } logo={ data.logo } />
            (
              // <div className="resturants-list">
              //   { data.map((restaurant) => (
              //     <CardRestaurant key={restaurant._id} restaurant={ restaurant } />
              //   ))}
              // </div>
            )
          )
        }
      </section>
    </>
  )
}

export default Restaurant;
