import React from 'react';
import useFetchData from '../hooks/useFetchData';


const Restaurant = (props) => {
  const URL = "http://localhost:4000/api/restaurants/";
  const { data, loading } = useFetchData(URL + props.match.params.id);

  return(
    <>
    <section>
    { (loading) ?  <p>Cargando...</p> :
        ((data.length === 0) ? <p>No hay restaurantes para mostrar.</p> :
          (
            <div className="resturants-list">
              { data.map((restaurant) => (
                <CardRestaurant key={restaurant._id} restaurant={ restaurant } />
              ))}
            </div>
          )
        )
      }

    </section>
    </>
  )
}

export default Restaurant;
