import React, { useState } from 'react';

const FormComment = () => {
  const [ state, setState ] = useState({
    name: "",
    comment: ""
  });

  const handleInputChange = e => {
    setState({
      ...state,
      [e.target.name]: e.target.value
    });
  }

  const handleSubmit = e => {
    e.preventDefault();
    const date = document.querySelector('#dpAddPostDate').value;
    const started = document.querySelector('#checkAddPostStarted').checked;

    if(!isNotEmpty([post.title, post.url])) {
      return false;
    }

    const data = { datos: {pu_titulo: post.title, pu_url: post.url, pu_fecha: date, pu_destacado: Number(!started)} }

    fetchData('https://acoacc.com/acoacc_api/public/post_publicacion', 'POST', token, data )
    .then( res => {
      if(res.status.type === 'error') {
        throw new Error( res.status );
      }
      toggleModal(e, 'modal-add-post');
      resetForm();
      alertFetchSuccess();
      handleSearch(e);
    })
    .catch( e => {
      alertFetchError();
    })
  }

